namespace DAT\Bannerslider\Controller\Adminhtml;

/**
 * Report Abstract Action
 * @category DAT
 * @package  DAT_Bannerslider
 * @module   Bannerslider
 * @author   DAT Developer
 */
abstract class Report extends \DAT\Bannerslider\Controller\Adminhtml\AbstractAction
{
    /**
     * Check if admin has permissions to visit related pages.
     *
     * @return bool
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('DAT_Bannerslider::bannerslider_reports');
    }
}
